import { useNavigate, useParams } from "react-router-dom";
import PokeballImg from "../assets/pokeball.png";
import Style from "./pokemon.module.css";
import Footer from "../components/Footer";
import { PokemonDetails } from "../types/types.d";
import { useEffect, useState } from "react";

const Pokemon = () => {
  const [pokemonDetails, setPokemonDetails] = useState<PokemonDetails>();
  const { name } = useParams();
  const navigateBack = useNavigate();

  useEffect(() => {
    const fetchPokemon = async (pokemonName: string) => {
      try {
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`);
        if (!response.ok) {
          throw new Error("Failed to fetch Pokemon details");
        }
        const data = await response.json();
        const pokemon: PokemonDetails = {
          name: data.name,
          id: data.id,
          imgSrc: data.sprites.front_default,
          hp: data.stats[0]?.base_stat,
          attack: data.stats[1]?.base_stat,
          defense: data.stats[2]?.base_stat,
        };
        setPokemonDetails(pokemon);
      } catch (error) {
        console.error(error);
      }
    };

    if (name) {
      fetchPokemon(name);
    }
  }, [name]);

  return (
    <>
      <button className={Style.pokeballButton} onClick={() => navigateBack(-1)}>
        <img className={Style.pokeballImg} src={PokeballImg} alt="pokeball" />
        Go Back
      </button>
      <div>
        <main className={Style.pokemonInfo}>
          <div className={Style.pokemonTitle}>{PokemonDetails.name?.toUpperCase()}</div>
          <div>{PokemonDetails.id}</div>
          <div> <img className={Style.pokemonInfoImg} src={PokemonDetails.imgSrc} /></div>
          <div>HP : {PokemonDetails.hp}</div>
          <div>Attack : {PokemonDetails.attack}</div>
          <div>Defense : {PokemonDetails.defense}</div>
        </main>
      </div>
      <Footer />
    </>
  );
};

export default Pokemon;
