import { useNavigate, useParams } from "react-router-dom";
import PokeballImg from "../assets/pokeball.png";
import Style from "./pokemon.module.css";
import Footer from "../components/Footer";
import { PokemonDetails } from "../types/types.d";
import { useEffect, useState } from "react";
import { fetchPokemon } from "../api/fetchPokemon";
import { formatName, waitFor } from "../utils/utils";

const Pokemon = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [pokemon, setPokemon] = useState<PokemonDetails>();
  const { pokemonName } = useParams();
  const navigateBack = useNavigate();

  useEffect(() => {
    async function getPokemon() {
      setIsLoading(true);
      await waitFor(1000);
      const fetchedPokemon = await fetchPokemon(pokemonName as string);
      setPokemon(fetchedPokemon);
      setIsLoading(false);
    };

    getPokemon();
  }, [pokemonName]);

  return (
    <>
      <button className={Style.pokeballButton} onClick={() => navigateBack(-1)}>
        <img className={Style.pokeballImg} src={PokeballImg} alt="pokeball" />
        Go Back
      </button>
      <div>
        <main className={Style.pokemonInfo}>
          <div className={Style.pokemonTitle}>{pokemon?.name?.toUpperCase()}</div>
          <div>Nr:. {pokemon?.id}</div>
          <div> <img className={Style.pokemonInfoImg} src={pokemon?.imgSrc} alt={pokemon?.name} /></div>
          <div>HP :. {pokemon?.hp}</div>
          <div>Attack :. {pokemon?.attack}</div>
          <div>Defense :. {pokemon?.defense}</div>
        </main>
      </div>
      <Footer />
    </>
  );
};

export default Pokemon;
